mapper.select:
SELECT ql1.*, qlp.projName, plu.realName FROM ql_AccessRecord01 ql1 JOIN ql_Project qlp ON ql1.projId = qlp.id JOIN plat_User plu ON ql1.creatorId = plu.id WHERE 1=1
and ql1.status = #{state}
and instr(ql1.custName , #{userName})

SELECT *
FROM ql_AccessRecord01 ql01
JOIN ql_Project qlp ON ql01.projId = qlp.id
WHERE ql01.custName = #{name}
AND ql01.custPhoneNum = #{phone}
AND qlp.id = #{project}
