mapper.insert:
insert into ql_UserCust_Ref (id, projId, userId, 
  custId, isValidate, cTime
  )
values (#{id,jdbcType=VARCHAR}, #{projid,jdbcType=VARCHAR}, #{userid,jdbcType=VARCHAR}, 
  #{custid,jdbcType=VARCHAR}, #{isvalidate,jdbcType=INTEGER}, #{ctime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT qur.*, qc.*, pu.realName, pu.isValidate,proj.projName,pu.isValidate FROM ql_UserCust_Ref qur JOIN ql_Customer qc ON qur.custId = qc.id JOIN plat_User pu ON qur.userId = pu.id join ql_Project proj on qur.projId = proj.id WHERE 1=1 and (instr(qc.custName,#{name}) OR instr(proj.projName, #{name}) OR instr(qc.phoneNum, #{name}) OR instr(pu.realName, #{name}))
and pu.isValidate = 3
limit #{page},#{pageSize}

SELECT qur.*, qc.*, pu.realName, pu.isValidate,proj.projName,pu.isValidate FROM ql_UserCust_Ref qur JOIN ql_Customer qc ON qur.custId = qc.id JOIN plat_User pu ON qur.userId = pu.id join ql_Project proj on qur.projId = proj.id WHERE 1=1 and (instr(qc.custName,#{name}) OR instr(proj.projName, #{name}) OR instr(qc.phoneNum, #{name}) OR instr(pu.realName, #{name}))
and INSTR(#{agegroup},qc.ageGroup)
and INSTR(#{liveacreage}, qc.liveAcreage)
and INSTR(#{pricesection}, qc.priceSection)
and INSTR(#{custscore}, qc.custScore)
and r01.recepTime > #{record01Begin}
and r01.recepTime < #{record01End}
and r02.recepTime > #{record02Begin}
and r02.recepTime < #{record02End}
and r03.recepTime > #{record03Begin}
and r03.recepTime < #{record03End}

SELECT qur.*, qc.*, pu.realName, pu.isValidate,proj.projName,pu.isValidate FROM ql_UserCust_Ref qur JOIN ql_Customer qc ON qur.custId = qc.id JOIN plat_User pu ON qur.userId = pu.id JOIN ql_Project proj ON qur.projId = proj.id JOIN ql_AccessRecord01 r01 ON qur.custId = r01.custId JOIN ql_AccessRecord01 r02 ON qur.custId = r02.custId JOIN ql_AccessRecord01 r03 ON qur.custId = r03.custId WHERE 1=1 and (instr(qc.custName,#{name}) OR instr(proj.projName, #{name}) OR instr(qc.phoneNum, #{name}) OR instr(pu.realName, #{name}))
and INSTR(#{agegroup},qc.ageGroup)
and INSTR(#{liveacreage}, qc.liveAcreage)
and INSTR(#{pricesection}, qc.priceSection)
and INSTR(#{custscore}, qc.custScore)
and r01.recepTime > #{record01Begin}
and r01.recepTime < #{record01End}
and r02.recepTime > #{record02Begin}
and r02.recepTime < #{record02End}
and r03.recepTime > #{record03Begin}
and r03.recepTime < #{record03End}
limit #{page},#{pageSize}

SELECT count(1) FROM ql_UserCust_Ref qur JOIN ql_Customer qc ON qur.custId = qc.id JOIN plat_User pu ON qur.userId = pu.id JOIN ql_Project proj ON qur.projId = proj.id JOIN ql_AccessRecord01 r01 ON qur.custId = r01.custId JOIN ql_AccessRecord01 r02 ON qur.custId = r02.custId JOIN ql_AccessRecord01 r03 ON qur.custId = r03.custId WHERE 1=1 and (instr(qc.custName,#{name}) OR instr(proj.projName, #{name}) OR instr(qc.phoneNum, #{name}) OR instr(pu.realName, #{name}))
and INSTR(#{agegroup},qc.ageGroup)
and INSTR(#{liveacreage}, qc.liveAcreage)
and INSTR(#{pricesection}, qc.priceSection)
and INSTR(#{custscore}, qc.custScore)
and r01.recepTime > #{record01Begin}
and r01.recepTime < #{record01End}
and r02.recepTime > #{record02Begin}
and r02.recepTime < #{record02End}
and r03.recepTime > #{record03Begin}
and r03.recepTime < #{record03End}
