mapper.select:
select * from plat_User where 1 = 1 
     
	and realName LIKE CONCAT(CONCAT('%', #{userName}),'%') 
	or nickname LIKE CONCAT(CONCAT('%', #{userName}),'%') 
	or mainPhoneNum LIKE CONCAT(CONCAT('%', #{userName}),'%') 
 
     
	and isValidate = #{state} 
 
limit #{page},#{pageSize}

select count(1) from plat_User where 1 = 1
and realName LIKE CONCAT(CONCAT('%', #{userName}),'%') or nickname LIKE CONCAT(CONCAT('%', #{userName}),'%') or mainPhoneNum LIKE CONCAT(CONCAT('%', #{userName}),'%')
and isValidate = #{state}
